import React, { useState, useEffect } from 'react';

const MortgageCalculator = () => {
    const [loanAmount, setLoanAmount] = useState(0);
    const [interestRate, setInterestRate] = useState(0);
    const [payment, setPayment] = useState(0);

    const handleClick = () => {
        let value = ((interestRate / 100) / 12) * loanAmount;
        console.log(0.00416 * 10000);

        setPayment(value);
    }

    return (
        <div className='io'>
            <div className='form-button-content'>
                <div className='contentUp'>
                    <div className='contentUpOne'>
                        <h2>Loan amount</h2>
                        <input type='text' pattern="\d*" onChange={(e) => setLoanAmount(parseFloat(e.target.value))} />
                    </div>
                    <div className='contentUpTwo'>
                        <h2>Interest rate</h2>
                        <input type='text' pattern="\d*" onChange={(e) => setInterestRate(parseFloat(e.target.value))} />
                    </div>
                </div>
                <div className='contentDown'>
                    <button onClick={() => handleClick()}>Calculate</button>
                </div>
            </div>

            <div className='monthly-payment'>
                <div className='monthly-payment-payment'>
                    ${payment === 0 ? 0 : payment.toFixed(2)}
                </div>
                <div className='monthly-payment-new'>
                    NEW MONTHLY PAYMENT
                </div>
            </div>
        </div>
    )
}

export default MortgageCalculator;
