import React, { useState, useEffect } from 'react';
import styles from './AddExtraPayments.module.css';
import plus from '../photos/plus.png';
import minus from '../photos/minus.png';
import { monthsInYear, nameOfMonths, years } from '../Mocks/mockData';

const AddExtraPayments = ({ setAddMonthlyPayment, setEveryMounthAmount, setEveryMounthName, setOneTimeAmount, setOneTimeMonth, setOneTimeYear, setIsApplyed }) => {
    const [isOpen, setIsOpen] = useState(false);

    const handleClick = () => {
        setIsOpen(!isOpen);
    }

    return (
        <div className={styles.mainWrapper}>
            <div className={styles.up}>
                <div className={styles.leftContent}>
                    {
                        !isOpen ?
                            <div className={styles.leftContentText}>
                                Add Extra Payments
                            </div>
                            :
                            <div className={styles.leftContentText}>
                                Hide Extra Payments
                            </div>
                    }
                </div>
                <div className={styles.rightContent}>
                    {!isOpen ? <a onClick={() => handleClick()}><img className={styles.plus} src={plus} alt='plus-logo' /></a> : null}
                    {isOpen ? <a onClick={() => handleClick()}><img className={styles.minus} src={minus} alt='minus-logo' /></a> : null}
                </div>
            </div>
            {isOpen &&
                <div className={styles.down}>
                    <div className={styles.downText}>
                        <div className={styles.downTextContent}>
                            Calculate the impact of extra payments using any combination of the
                        </div>
                        <div className={styles.downTextContent}>
                            inputs below. To see your new estimated payoff date, click ‘Show
                        </div>
                        <div className={styles.downTextContent}>
                            amortization schedule’ link above.
                        </div>
                    </div>
                    <div className={styles.downInput}>
                        <div className={styles.inputText}>
                            Amount to your monthly payment
                        </div>
                        <div className={styles.inputs}>
                            <input type='number' placeholder='$' onChange={(e) => setAddMonthlyPayment(e.target.value)} />
                        </div>
                        <div className={styles.inputText}>
                            Amount as an extra yearly payment occurring every:
                        </div>
                        <div className={styles.inputs}>
                            <input type='number' placeholder='$' onChange={(e) => setEveryMounthAmount(e.target.value)} />
                            <select onChange={(e) => setEveryMounthName(e.target.value)}>
                                {
                                    nameOfMonths.map(month => {
                                        return (
                                            <option>{month}</option>
                                        )
                                    })
                                }
                            </select>
                        </div>
                        <div className={styles.inputText}>
                            Amount as a one-time payment in:
                        </div>
                        <div className={styles.inputs}>
                            <input type='number' placeholder='$' onChange={(e) => setOneTimeAmount(e.target.value)} />
                            <select onChange={(e) => setOneTimeMonth(e.target.value)}>
                                {
                                    nameOfMonths.map(month => {
                                        return (
                                            <option>{month}</option>
                                        )
                                    })
                                }
                            </select>
                            <select style={{ width: '80px' }} onChange={(e) => setOneTimeYear(e.target.value)}>
                                {
                                    years.map(year => {
                                        return (
                                            <option>{year}</option>
                                        )
                                    })
                                }
                            </select>
                        </div>
                    </div>
                    <div className={styles.downButton}>
                        <button onClick={() => setIsApplyed(true)}>Apply Extra Payments</button>
                    </div>
                </div>
            }
        </div>
    )
}

export default AddExtraPayments;
