import React, { useState, useEffect } from 'react';

const IO = () => {
    const [loanAmount, setLoanAmount] = useState(0);
    const [interestRate, setInterestRate] = useState(0);
    const [payment, setPayment] = useState(0);
    return (
        <div className='io'>
            <div className='form-button-content'>
                <div className='contentUp'>
                    <div className='contentUpOne'>
                        <h2>Loan amount</h2>
                        <input type='text' placeholder='$300000' onChange={(e) => setLoanAmount(e.target.value)} />
                    </div>
                    <div className='contentUpTwo'>
                        <h2>Interest rate</h2>
                        <input type='text' placeholder='3' onChange={(e) => setInterestRate(e.target.value)} />
                    </div>
                </div>
                <div className='contentDown'>
                    <button>Calculate</button>
                </div>
            </div>

            <div className='monthly-payment'>
                <h3>${payment}</h3>
                <p>NEW MONTHLY PAYMENT</p>
            </div>
        </div>
    )
}

export default IO;
